import React, { useState, useEffect } from 'react';



const Background = () => {
    //Hook to get api
    const [posts, setPosts, setCurrentTime] = useState([]);
    useEffect(() => {
       fetch('http://worldtimeapi.org/api/ip')
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            var hours = new data.getHours(); //Current Hours
            var min = new data.getMinutes(); //Current Minutes
            var sec = new data.getSeconds(); //Current Seconds
             setCurrentTime(hours + ':' + min + ':' + sec)
             setPosts(data);
          })
          .catch((err) => {
             console.log(err.message);
          });
    }, []);

    return (
        <div className="posts-container">
            <h1>Time {posts.unixtime}</h1>
            <h1>Day of the week {posts.day_of_week}</h1>
            <h1>Day of the year {posts.day_of_year}</h1>
            <h1>Week Number {posts.week_number}</h1>
            <h1>Current Timezone {posts.timezone}</h1>
        </div>
    );
 };

    
export default Background;
