import React, { useState, useEffect } from 'react';
import App from '../App';
import bgImgDayDesktop from '../assets/bg-image-daytime-desktop.jpg';
import bgImgDayMobile from '../assets/bg-image-daytime-mobile.jpg';
import bgImgDayTablet from '../assets/bg-image-daytime-tablet.jpg';

import bgImgNightDesktop from '../assets/bg-image-nighttime-desktop.jpg';
import bgImgNightMobile from '../assets/bg-image-nighttime-mobile.jpg';
import bgImgNightTablet from '../assets/bg-image-nighttime-tablet.jpg';

const Backgroundimg = () => {

    const [posts, setPosts] = useState([]);
    useEffect(() => {
       fetch('http://worldtimeapi.org/api/ip')
          .then((response) => response.json())
          .then((data) => {
             console.log(data);
             setPosts(data);
          })
          .catch((err) => {
             console.log(err.message);
          });
    }, []);
 
    return (
        <div className="posts-container">
        {posts.map((post) => {
            return (
                <div className="post-card" key={post.id}>
                <h2 className="post-title">{post.title}</h2>
                <p className="post-body">{post.body}</p>
                <div className="button">
                <div className="delete-btn">Delete</div>
                </div>
                </div>
            );
        })}
    </div>
    );
 };

    
export default Backgroundimg;
