import React, { useState, useEffect } from 'react';



const Background = () => {
    //Hook to get api
    const [posts, setPosts] = useState([]);
    useEffect(() => {
       fetch('http://worldtimeapi.org/api/ip')
          .then((response) => response.json())
          .then((data) => {
            console.log(data);
            console.log(data.unixtime);
             setPosts(data);
          })
          .catch((err) => {
             console.log(err.message);
          });
    }, []);

    const timedisplay = ReactDOM.createRoot(
        document.getElementById('Time-display')
      );
      
      function tick() {
        const element = (
          <div>
            <h1>Hello, world!</h1>
            <h2>It is {data.unixtime.toLocaleTimeString()}.</h2>
          </div>
        );
        timedisplay.render(element);
      }
      
      setInterval(tick, 1000);

    return (
        <div className="posts-container">
            <h1>Time {posts.unixtime}</h1>
            <h1>Day of the week {posts.day_of_week}</h1>
            <h1>Day of the year {posts.day_of_year}</h1>
            <h1>Week Number {posts.week_number}</h1>
            <h1>Current Timezone {posts.timezone}</h1>
        </div>
    );
 };

    
export default Background;
