import React, { useState, useEffect } from 'react';
import dayjs from "dayjs";

const Background = () => {
    const dayjs = require('dayjs')
    //import dayjs from 'dayjs' // ES 2015
    //dayjs().format()
    //var locale_de = require('dayjs/locale/de')
    // import locale_de from 'dayjs/locale/de'  // ES 2015 
    

    //Hook to get api
    const [posts, setPosts] = useState([
      
    ]);
    useEffect(() => {
        const timer = setInterval(() => {
            fetch('http://worldtimeapi.org/api/ip')
            .then((response) => response.json())
            .then((data) => {
                console.log(data);
                console.log(data.unixtime);
                setPosts(data);
            })
            .catch((err) => {
                console.log(err.message);
            });
        }, 6000);
        return () => clearInterval(timer);
    }, []);


    const date = new Date();
    const [dateTime, setDateTime] = useState({
        hours: date.getHours(),
        minutes: date.getMinutes(),
        seconds: date.getSeconds()
    });
    useEffect(() => {
        const timer = setInterval(() => {
            const date = new Date();
            setDateTime({
                hours: date.getHours(),
                minutes: date.getMinutes(),
                seconds: date.getSeconds()
            });
        }, 6000);
        return () => clearInterval(timer);
    }, []);
    
    var settime = posts.unixtime;
    var localizedFormat = require('dayjs/plugin/localizedFormat')
    dayjs.extend(localizedFormat)

    var formattedDate = dayjs(settime).format('L LT');
    console.log("settime" + formattedDate);

    return (
        <div className="posts-container">
            <h1>{dateTime.hours}:{dateTime.minutes}:{dateTime.seconds}</h1>
            <h1>Time {formattedDate}</h1>
            <h1>Day of the week {posts.day_of_week}</h1>
            <h1>Day of the year {posts.day_of_year}</h1>
            <h1>Week Number {posts.week_number}</h1>
            <h1>Current Timezone {posts.timezone}</h1>
        </div>
    );
 };

    
export default Background;
